import React, { useContext, useState } from "react";
import MealsContext from "../store/meals-context";

function Meal(props) {
  const mealsContext = useContext(MealsContext);
  const [amount, setAmount] = useState(1);
  const amountHandler = (event) => {
    setAmount(event.target.value);
  };
  const addToCart = () => {
    mealsContext.amountTotal += +amount;
    mealsContext.addAmountTotal(mealsContext.amountTotal);
    // const index= mealsContext.meals.findIndex((item) => item.id === props.meal.id)
    // console.log(index)
    // if(index >= 0) {
    //   mealsContext.meals[index] = {
    //     ...mealsContext.meals[index],
    //     amount: mealsContext.meals[index].amount += +amount
    //   }
    //   } else {
    //     mealsContext.addMeals({
    //       id: props.meal.id,
    //       amount: +amount,
    //       name: props.meal.name,
    //     });
    //   };
    mealsContext.meals.length > 0 && mealsContext.meals.forEach((itemMeal) => {
      // console.log(itemMeal+"vvvvvvvvvv");
      if (itemMeal.id === props.meal.id) {
        itemMeal = {
          ...itemMeal,
          
        }
      } else {
        mealsContext.addMeals({
          id: props.meal.id,
          amount: amount,
          name: props.meal.name,
        });
      }
    });
    setAmount(1);
  };
  return (
    <div>
      <input
        type="number"
        step={1}
        min={0}
        onChange={amountHandler}
        value={amount}
      />
      <button onClick={addToCart}>add</button>
    </div>
  );
}

export default Meal;
