import { Fragment, useState } from "react";
import Cart from "./component/Cart/Cart";
import Header from "./component/Layout/Header";
import Meals from "./component/Meals/Meals";
import MealsContext from "./component/store/meals-context";

function App() {
  const [amountTotal, setAmountTotal] = useState(0);
  const [meals, setMeals] = useState([]);
  const [open, setOpen] = useState(false)
  const addAmountTotal = (number) => {
    setAmountTotal(number);
  };

  const addMeals = (meal) => {
    const objMeals = [...meals, meal];
    setMeals(objMeals);
  };

  return (
    <MealsContext.Provider
      value={{
        amountTotal: amountTotal,
        meals: meals,
        addAmountTotal: addAmountTotal,
        addMeals: addMeals,
        open: open
      }}
    >
      <Header />
      <main>
        {!open&&<Meals />}
        {open && <Cart/>}
      </main>
    </MealsContext.Provider>
  );
}

export default App;
